import React, {Component} from 'react';
import Particles from 'react-particles-js';
import {Navigation, Logo, ImageLinkForm, Rank, FaceRecognition} from './Imports';
import './App.css';
import Clarifai from 'clarifai';


// creating an instanciation of the Clarifai object imported
const app = new Clarifai.App({
 apiKey: 'fcd3c135178c4f1980cfb9beac698cdd'
});



const particlesOptions = {
particles: {
	number: {
		value:40,
		density: {
			enable:true,
			value_area: 800
		}
	}
}
}


class App extends Component {
	constructor(){
		super();
		this.state = {
			input:'',
			imageURL:'',
			box: {},
		}
	}

	calculateFaceBox = (dataReceived) => {
		const clarifaiFaceLoc = 
			dataReceived.outputs[0].data.regions[0].region_info.bounding_box;
		const image = document.getElementById('inputImage');
		const width = Number(image.width);
		const height = Number(image.height);
		return {
			leftCol: clarifaiFaceLoc.left_col * width,
			topRow: clarifaiFaceLoc.top_row *height,
			rightCol: width - (clarifaiFaceLoc.right_col * width),
			bottomRow: height - (clarifaiFaceLoc.bottom_row * height)
		}
	}

	displayFaceBox = (faceBoxObject) => {
		console.log(faceBoxObject);
		this.setState({box: faceBoxObject});
	}

	onInputChange = (event) => {
		this.setState({input: event.target.value})
	}

	onButtonSubmit = (event) => {
		this.setState({imageURL: this.state.input})
		app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
			//if instead of typing this.state.input I would have typed this.state.imageURL, I would have gotten an error
			.then(response => this.displayFaceBox(this.calculateFaceBox(response)))
			.catch(err => console.log(err));
	}

	render(){
	  return (
	    <div className="App">
	    	<Particles className='particles' params={particlesOptions}/>
	    	<Navigation />
			<Logo />
			<Rank />
	    	<ImageLinkForm 
	    		onInputChange = {this.onInputChange}
	    		onButtonSubmit = {this.onButtonSubmit}
	    	/>

	    	<FaceRecognition box={this.state.box} imageURL={this.state.imageURL}/>
	    </div>
	  );
	 }
}

export default App;
