import React, {Component} from 'react';
import Particles from 'react-particles-js';
import {Navigation, 
		Logo, 
		ImageLinkForm, 
		Rank, 
		FaceRecognition,
		SignIn,
		Register,
	} from './Imports';
import './App.css';
import Clarifai from 'clarifai';

// creating an instanciation of the Clarifai object imported
const app = new Clarifai.App({
 apiKey: 'fcd3c135178c4f1980cfb9beac698cdd'
});



const particlesOptions = {
particles: {
	number: {
		value:40,
		density: {
			enable:true,
			value_area: 800
		}
	}
}
}


class App extends Component {
	constructor(){
		super();
		this.state = {
			input:'',
			imageURL:'',
			boxes: [],
			route: 'signin',
			isSignedIn: false,
		}
	}

	// this is my solution to the multi face images

	calculateArrayOfFacesBox = (dataReceived) =>{
		const image = document.getElementById('inputImage');
		const width = Number(image.width);
		const height = Number(image.height);

		const arrayOfLocs = 
			dataReceived.outputs[0].data.regions.map(region =>{
				let currentRegion = region.region_info.bounding_box;
				return {
					leftCol: currentRegion.left_col * width,
					topRow: currentRegion.top_row *height,
					rightCol: width - (currentRegion.right_col * width),
					bottomRow: height - (currentRegion.bottom_row * height)
				}
			})
		return arrayOfLocs;
	}


	displayFaceBoxes = (faceBoxObjectArray) => {
		this.setState({boxes: faceBoxObjectArray});
	}

	onInputChange = (event) => {
		this.setState({input: event.target.value})
	}

	onButtonSubmit = (event) => {
		this.setState({imageURL: this.state.input})
		app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
			//if instead of typing this.state.input I would have typed this.state.imageURL, I would have gotten an error
			// .then(response => this.displayFaceBox(this.calculateFaceBox(response)))
			.then(response => this.displayFaceBoxes(this.calculateArrayOfFacesBox(response)))
			.catch(err => console.log(err));
	}

	onRouteChange = (route) => {
		this.setState({route: route});
	}

	render(){
	const staticParts = (
		<div>
			<Particles className='particles' params={particlesOptions}/>
	    	<Navigation onRouteChange={this.onRouteChange} 
	    		isSignedIn = {this.state.isSignedIn}/>
	    </div>
	)
	return (
	    <div className="App">
	    	{staticParts}
	    	{ this.state.route === 'home'
	    		? <div>
					<Logo />
					<Rank />
			    	<ImageLinkForm 
			    		onInputChange = {this.onInputChange}
			    		onButtonSubmit = {this.onButtonSubmit}
			    	/> 
			    	<FaceRecognition 
			    		boxes={this.state.boxes} 
			    		imageURL={this.state.imageURL}
			    	/>
			      </div>
	    		: ( this.state.route === 'signin'
	    			? <SignIn onRouteChange={this.onRouteChange}/>
	    			: <Register onRouteChange={this.onRouteChange}/>
	    		  )
		    }
	    </div>
	  );
	 }
}

export default App;
