import React, {Component} from 'react';
import Particles from 'react-particles-js';
import {Navigation, Logo, ImageLinkForm, Rank, FaceRecognition} from './Imports';
import './App.css';
import Clarifai from 'clarifai';


// creating an instanciation of the Clarifai object imported
const app = new Clarifai.App({
 apiKey: 'fcd3c135178c4f1980cfb9beac698cdd'
});



const particlesOptions = {
particles: {
	number: {
		value:40,
		density: {
			enable:true,
			value_area: 800
		}
	}
}
}


class App extends Component {
	constructor(){
		super();
		this.state = {
			input:'',
			imageURL:'',
			box: {},
		}
	}

	calculateFaceBox = (dataReceived) => {
		const clarifaiFaceLoc = 
			dataReceived.outputs[0].data.regions[0].region_info.bounding_box;
		const image = document.getElementById('inputImage');
		const width = Number(image.width);
		const height = Number(image.height);
		console.log(width, height);
	}

	onInputChange = (event) => {
		this.setState({input: event.target.value})
	}

	onButtonSubmit = (event) => {
		// console.log('button clicked');
		this.setState({imageURL: this.state.input})

		//remember this is a promise
		app.models.predict(
			Clarifai.FACE_DETECT_MODEL, 
			//if instead of typing this.state.input I would have typed this.state.imageURL, I would have gotten an error
			this.state.input)
			.then(response => this.calculateFaceBox(response))
			.catch(err => console.log(err));
	}

	render(){
	  return (
	    <div className="App">
	    	<Particles className='particles' params={particlesOptions}/>
	    	<Navigation />
			<Logo />
			<Rank />
	    	<ImageLinkForm 
	    		onInputChange = {this.onInputChange}
	    		onButtonSubmit = {this.onButtonSubmit}
	    	/>

	    	<FaceRecognition imageURL={this.state.imageURL}/>
	    </div>
	  );
	 }
}

export default App;
