import React, {Component} from 'react';
import Particles from 'react-particles-js';
import {Navigation, 
		Logo, 
		ImageLinkForm, 
		Rank, 
		FaceRecognition,
		SignIn,
		Register,
	} from './Imports';
import './App.css';
import Clarifai from 'clarifai';

// creating an instanciation of the Clarifai object imported
const app = new Clarifai.App({
 apiKey: 'fcd3c135178c4f1980cfb9beac698cdd'
});



const particlesOptions = {
particles: {
	number: {
		value:40,
		density: {
			enable:true,
			value_area: 800
		}
	}
}
}


class App extends Component {
	constructor(){
		super();
		this.state = {
			input:'',
			imageURL:'',
			boxes: [],
			route: 'signin',
			isSignedIn: false,
			user: {
				id:'',
				name: '',
				email: '',
				entries: 0,
				joined: ''
			}
		}
	}

	loadUser = (data) => {
		this.setState({user: {
			id: data.id,
			name: data.name,
			email: data.email,
			entries: data.entries,
			joined: data.joined
		}})
		console.log(this.state)
	}

	componentDidMount() {
		fetch('http://localhost:3000/')
			.then(response => response.json())
			.then(response => console.log(response))
	}


	// this is my solution to the multi face images

	calculateArrayOfFacesBox = (dataReceived) =>{
		const image = document.getElementById('inputImage');
		const width = Number(image.width);
		const height = Number(image.height);

		const arrayOfLocs = 
			dataReceived.outputs[0].data.regions.map(region =>{
				let currentRegion = region.region_info.bounding_box;
				return {
					leftCol: currentRegion.left_col * width,
					topRow: currentRegion.top_row *height,
					rightCol: width - (currentRegion.right_col * width),
					bottomRow: height - (currentRegion.bottom_row * height)
				}
			})
		return arrayOfLocs;
	}


	displayFaceBoxes = (faceBoxObjectArray) => {
		this.setState({boxes: faceBoxObjectArray});
	}

	onInputChange = (event) => {
		this.setState({input: event.target.value})
	}

	onButtonSubmit = (event) => {
		this.setState({imageURL: this.state.input})
		app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
			//if instead of typing this.state.input I would have typed this.state.imageURL, I would have gotten an error
			// request failed
			// .then(response => this.displayFaceBox(this.calculateFaceBox(response)))
			.then(response => {
				if(response){
				    event.preventDefault();
				    // i want to increase the amount of entries
				    // by sending a request to the backend
				    // im telling that i entered an image
				    fetch('http://localhost:3000/image', {
				      method: 'put',
				      headers: {'Content-Type': 'application/json'},
				      body: JSON.stringify({
				        id: this.state.id
				      })
				    })
				    // the response is transformed to json
				    .then(res => {
				    	console.log(res)
				    	res.json()
				    })
				    // then i reassign the value of entries
				    .then(count => {
				    	this.setState(
				    		// i only want to modify a property
				    		// not all of them
				    		Object.assign(this.state.user, {entries: count}))
				    })
				}
				this.displayFaceBoxes(
					this.calculateArrayOfFacesBox(response))
			})
			.catch(err => console.log(err));
	}

	onRouteChange = (route) => {
		if (route === 'signout'){
			this.setState({isSignedIn: false});
		} else if (route === 'home') {
			this.setState({isSignedIn: true});
		}
		this.setState({route: route});
	}

	render(){
	const staticParts = (
		<div>
			<Particles className='particles' params={particlesOptions}/>
	    	<Navigation onRouteChange={this.onRouteChange} 
	    		isSignedIn = {this.state.isSignedIn}/>
	    </div>
	)
	return (
	    <div className="App">
	    	{staticParts}

	    	{ this.state.route === 'home'
	    		? <div>
					<Logo />
					<Rank 
						userName={this.state.user.name}
						userEntries={this.state.user.entries}
						/>
			    	<ImageLinkForm 
			    		onInputChange = {this.onInputChange}
			    		onButtonSubmit = {this.onButtonSubmit}
			    	/> 
			    	<FaceRecognition 
			    		boxes={this.state.boxes} 
			    		imageURL={this.state.imageURL}
			    	/>
			      </div>
	    		: ( this.state.route === 'signin'
	    			? <SignIn 
	    						loadUser={this.loadUser}
	    						onRouteChange={this.onRouteChange}/>
	    			: <Register 
	    						loadUser={this.loadUser}
	    						onRouteChange={this.onRouteChange}/>
	    		  )
		    }

	    </div>
	  );
	 }
}

export default App;
